mapper.select:
SELECT 
FROM (
  (SELECT 
   FROM outcome
   WHERE payer_userid = #{userId}
     AND deleted_flag = 0
     
       AND id < #{lastId}
     
     
       AND pay_datetime >= #{monthStart}
     
     
       AND pay_datetime < #{monthEnd}
     
  )
  UNION ALL
  (SELECT 
   FROM outcome
   WHERE target_userid = #{userId}
     AND repay_flag = 2
     AND deleted_flag = 0
     
       AND id < #{lastId}
     
     
       AND pay_datetime >= #{monthStart}
     
     
       AND pay_datetime < #{monthEnd}
     
  )
) combined
ORDER BY id DESC
LIMIT #{limit}

SELECT COALESCE(SUM(amount), 0)
FROM outcome
WHERE payer_userid = #{userId}
  AND deleted_flag = 0
  AND repay_flag = 1
  AND pay_datetime >= #{monthStart}
  AND pay_datetime < #{monthEnd}
